document.addEventListener("DOMContentLoaded", function() {
    // The code inside this block will run after the DOM is fully loaded

    const ageInput = document.getElementById("age");
    const genderInput = document.getElementById("gender");
    const hobbiesInput = document.getElementById("hobbies");
    const funWithFriendsInput = document.getElementById("funWithFriends");
    const nextButton = document.getElementById("nextButton");

    // Define an array of input elements
    const inputs = [ageInput, genderInput, hobbiesInput, funWithFriendsInput];

    // Add an event listener to each input element to check for changes
    inputs.forEach(input => {
      input.addEventListener("input", checkFields);
    });

    // Function to check if all fields are filled and enable the "Next" button
    function checkFields() {
      const allFieldsFilled = inputs.every(input => input.value.trim() !== "");
      nextButton.disabled = !allFieldsFilled;
    }

    // When the "next" button is clicked, collect the responses and save them in a JSON object
    nextButton.addEventListener("click", function() {
      const responses = {
        age: ageInput.value,
        gender: genderInput.value,
        hobbies: hobbiesInput.value,
        funWithFriends: funWithFriendsInput.value,
      };

      // Convert the responses to a JSON string
      const responsesJSON = JSON.stringify(responses);

      // Save the JSON string to local storage
      localStorage.setItem("userResponses", responsesJSON);

      // Redirect to the next page (replace 'next.html' with your desired next page)
      window.location.href = "next.html";
    });
  });
